@use "colors" as c;
@use "mixins" as m;
@use "fonts" as f;

// hamburger
.hamburger {
  width: 1.5rem;
  display: flex;
  flex-direction: column;
  gap: 0.25rem;

  span {
    display: flex;
    width: 100%;
    height: 3px;
    background: c.$black;
    border-radius: 0.25rem;

    &:last-child {
      width: 0.75rem;
      align-self: flex-end;
    }
  }
}

// badge
.badge {
  background-color: c.$black;
  @include m.circle(20px);
  font-size: 10px;
  color: c.$white;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: f.$font-medium;
}

// button
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 30px;
  height: 60px;
  width: 240px;
  text-decoration: none;

  &--black {
    background-color: c.$black;
    color: c.$white;
  }
}

